let stairConstructorImage = {
	"carcas": "mono-cosour_",
	"form": "straight_",
	"color": "black_",
	"material": "beech_",
	"fences": "forging"
};

let imageCollectOut = '';
let imageSrc = '';
let resoultImage = document.querySelector('.stair-constructor_resolt img');

function imageCollect() {
	imageCollectOut = "";
	for (let key in stairConstructorImage) {
		imageCollectOut += stairConstructorImage[key];
	}
	imageShowPage();
	return imageCollectOut;
}

function imageShowPage() {
	imageSrc = '';
	imageSrc += `assets/img/stair-constructor/resoult/${imageCollectOut}.jpg`;
	resoultImage.src = imageSrc;
}

let stairConstructorRadioColor = document.querySelectorAll('.stair-constructor_color .stair-constructor_radio');

for (let i = 0; i < stairConstructorRadioColor.length; i++) {
	stairConstructorRadioColor[i].onchange = getRadioColor;
}

function getRadioColor() {
	stairConstructorImage.color = this.value;
	imageCollect();
}

let stairConstructorRadioMaterial = document.querySelectorAll('.stair-constructor_material .stair-constructor_radio');

for (let i = 0; i < stairConstructorRadioMaterial.length; i++) {
	stairConstructorRadioMaterial[i].onchange = getRadioMaterial;
}

function getRadioMaterial() {
	stairConstructorImage.material = this.value;
	imageCollect();
}

let stairConstructorRadioFences = document.querySelectorAll('.stair-constructor_fences .stair-constructor_radio');

for (let i = 0; i < stairConstructorRadioFences.length; i++) {
	stairConstructorRadioFences[i].onchange = getRadioFences;
}

function getRadioFences() {
	stairConstructorImage.fences = this.value;
	imageCollect();
}